// Extract images from JSON body (no prompt needed)
// Frontend sends JSON with base64-encoded images
const items = $input.all();
const item = items[0];

// Debug: Log what we received
console.log('Received item keys:', Object.keys(item));
console.log('item.json keys:', item.json ? Object.keys(item.json) : 'no json');

// Extract data from JSON body
let imageBase64Array = [];

// Try multiple ways to extract the data (n8n webhook can structure data differently)
let bodyData = null;

// Method 1: Check if data is directly in item.json
if (item.json && item.json.images) {
  bodyData = item.json;
  console.log('Method 1: Using direct item.json data');
}

// Method 2: Check if body exists and is already an object
if (!bodyData && item.json && item.json.body) {
  bodyData = item.json.body;

  // If body is a string, parse it
  if (typeof bodyData === 'string') {
    try {
      bodyData = JSON.parse(bodyData);
      console.log('Method 2: Parsed body from string');
    } catch (e) {
      console.error('Failed to parse JSON body:', e.message);
      bodyData = null;
    }
  } else if (typeof bodyData === 'object' && bodyData.images) {
    console.log('Method 2: Using item.json.body as object');
  } else {
    bodyData = null;
  }
}

// Method 3: Check item.body directly
if (!bodyData && item.body) {
  bodyData = typeof item.body === 'string' ? JSON.parse(item.body) : item.body;
  console.log('Method 3: Using item.body data');
}

// Extract images from bodyData
if (bodyData) {
  // Extract images
  if (bodyData.images && Array.isArray(bodyData.images)) {
    // Images are already base64 data URLs from frontend
    imageBase64Array = bodyData.images.slice(0, 5); // Limit to 5 images
    console.log('Found', imageBase64Array.length, 'images');
  }
} else {
  console.warn('No body data found in any expected location');
}

console.log('Final image count:', imageBase64Array.length);
if (imageBase64Array.length > 0) {
  console.log('First image preview:', imageBase64Array[0].substring(0, 100) + '...');
}

return {
  json: {
    images: imageBase64Array,
    imageCount: imageBase64Array.length,
    hasImages: imageBase64Array.length > 0
  }
};

// Create 8 predefined prompts, each duplicated 3 times (24 total requests)
// This allows parallel processing of 24 API calls
const items = $input.all();
const item = items[0];

// 8 predefined prompts
const prompts = [
  'keep all the same, but choose a nice background, and nice clothes',
  'keep all the same, but choose a nice studio background, and nice clothes',
  'keep all the same, but choose a nice business background, and nice business clothes',
  'keep all the same, but choose a nice street background, and nice street clothes',
  'keep all the same, but choose a nice dating background, and nice dating clothes',
  'choose a nice studio background, and nice clothes High-definition, 4K quality, ultra-realistic skin texture',
  'An ultra realistic portrait of this person in the attached image, maintaining his physical characteristics and consistency of the character, wearing a navy blue cargo jacket over a white t-shirt. Soft navy blue gradient background, studio light rambrandt style, cinematic, contrast, 4k, realistic, detailed, high sharpness, professional skin retouching, 4:5 radius',
  'Transform this photos into a professional studio-lit headshot. Maintain exact facial features and identity. Wear a high-quality, fitted grey crew-neck long-sleeve shirt with a subtle texture. Neutral, calm expression with direct eye contact. Lighting should be soft studio flash from the side, creating gentle shadows for depth. Set against a clean, charcoal-grey backdrop with a slight gradient. High-definition, 4K quality, ultra-realistic skin texture'
];

// Create items: 8 prompts × 3 duplicates = 24 items
const duplicatedItems = [];
let itemIndex = 0;

for (let promptIndex = 0; promptIndex < prompts.length; promptIndex++) {
  const prompt = prompts[promptIndex];

  // Duplicate each prompt 3 times
  for (let duplicateIndex = 0; duplicateIndex < 3; duplicateIndex++) {
    itemIndex++;
    duplicatedItems.push({
      json: {
        ...item.json,
        prompt: prompt,
        promptIndex: promptIndex + 1,
        duplicateIndex: duplicateIndex + 1,
        requestIndex: itemIndex,
        totalRequests: 24
      }
    });
  }
}

console.log('Created', duplicatedItems.length, 'items (8 prompts × 3 duplicates)');
console.log('Prompts:', prompts.length);

return duplicatedItems;

// Prepare request body for OpenRouter Gemini 2.5 Flash Image
// Format matches OpenRouter API documentation: https://openrouter.ai/google/gemini-2.5-flash-image
// Process ALL items (should be 24 from Create Prompts node)
const items = $input.all();

console.log('Prepare Request - Processing', items.length, 'item(s)');

// Process each item and prepare request body for each
const preparedRequests = [];

for (let i = 0; i < items.length; i++) {
  const item = items[i];

  // Debug: Log what we received
  console.log(`Prepare Request ${i + 1} - promptIndex:`, item.json?.promptIndex, 'duplicateIndex:', item.json?.duplicateIndex);
  console.log(`Prepare Request ${i + 1} - prompt:`, item.json?.prompt?.substring(0, 100));

  // Build the request payload exactly as per OpenRouter docs
  // IMPORTANT: For image generation, we MUST include modalities: ["image", "text"]
  const requestBody = {
    model: 'google/gemini-2.5-flash-image',
    modalities: ['image', 'text'], // Required for image generation
    messages: [
      {
        role: 'user',
        content: []
      }
    ]
  };

  // Get the prompt
  let promptText = item.json?.prompt || 'Create a professional, high-quality portrait photo';

  console.log(`Prepare Request ${i + 1} - Final prompt to use:`, promptText.substring(0, 100));

  // Build content array - images first, then text (as per OpenRouter example)
  const contentArray = [];

  // Add images if available
  if (item.json.images && item.json.images.length > 0) {
    item.json.images.forEach((imageDataUrl, index) => {
      if (index < 5) { // Limit to 5 images
        contentArray.push({
          type: 'image_url',
          image_url: {
            url: imageDataUrl
          }
        });
      }
    });
  }

  // Add text prompt (required)
  contentArray.push({
    type: 'text',
    text: promptText
  });

  // Set the content array
  requestBody.messages[0].content = contentArray;

  // Log for debugging
  console.log(`Prepare Request ${i + 1} - Request model:`, requestBody.model);
  console.log(`Prepare Request ${i + 1} - Content items:`, contentArray.length);
  console.log(`Prepare Request ${i + 1} - Images count:`, item.json.images ? item.json.images.length : 0);

  preparedRequests.push({
    json: requestBody
  });
}

console.log('Prepare Request - Prepared', preparedRequests.length, 'request(s)');

// Return all prepared requests (should be 24)
return preparedRequests;

// Extract images from all responses (memory-efficient)
// Process all items but only extract image URLs to save memory
const items = $input.all();

console.log('Process Response: Processing', items.length, 'item(s)');

// Process each item and extract only the image URL (not full response)
const processedItems = [];

for (let i = 0; i < items.length; i++) {
  const item = items[i];
  let imageUrl = '';
  let imageBase64 = '';
  let error = null;

  // Ensure we have valid data
  if (!item || !item.json) {
    processedItems.push({
      json: {
        success: false,
        error: 'No data received from OpenRouter API',
        imageUrl: null
      }
    });
    continue;
  }

  try {
    // Check for error first
    if (item.json.error) {
      const errorMsg = item.json.error.message || item.json.error.error?.message || JSON.stringify(item.json.error);
      error = 'OpenRouter API Error: ' + errorMsg;
      console.error(`Item ${i + 1} API error:`, errorMsg);
      processedItems.push({
        json: {
          success: false,
          error: error,
          imageUrl: null
        }
      });
      continue;
    }

    // Check for direct image generation response format (images/generations endpoint)
  if (item.json.data && Array.isArray(item.json.data) && item.json.data[0]) {
    imageUrl = item.json.data[0].url || '';
    imageBase64 = item.json.data[0].b64_json || '';
  }
  // Check for chat completions response format
  // For image generation, images are in choices[0].message.images array
  else if (item.json.choices && item.json.choices[0]) {
    const choice = item.json.choices[0];

    // First, check for images array (this is the correct format for image generation)
    if (choice.message && choice.message.images && Array.isArray(choice.message.images)) {
      const firstImage = choice.message.images[0];
      if (firstImage) {
        // Image format: { type: "image_url", image_url: { url: "data:image/png;base64,..." } }
        imageUrl = firstImage.image_url?.url || firstImage.url || '';
        imageBase64 = firstImage.image_url?.url || firstImage.url || '';
      }
    }
    // Fallback: check content field (for text-only responses)
    else if (choice.message && choice.message.content) {
      const content = choice.message.content;

      // Handle array content (multimodal)
      if (Array.isArray(content)) {
        const imageContent = content.find(c => c.type === 'image_url' || c.type === 'image');
        if (imageContent) {
          imageUrl = imageContent.image_url?.url || imageContent.url || '';
          imageBase64 = imageContent.image_url?.url || imageContent.url || '';
        }
        // Also check for text content that might contain image data
        const textContent = content.find(c => c.type === 'text');
        if (textContent && textContent.text && textContent.text.includes('data:image')) {
          imageBase64 = textContent.text;
        }
      }
      // Handle string content
      else if (typeof content === 'string') {
        if (content.includes('data:image')) {
          imageBase64 = content;
        } else if (content.startsWith('http')) {
          imageUrl = content;
        }
      }
    }

    // If still no image found, check for alternative response structures
  if (!imageUrl && !imageBase64) {
    // Check for nested data structures
    if (item.json.result && item.json.result.image) {
      imageUrl = item.json.result.image;
    } else if (item.json.image) {
      imageUrl = item.json.image;
    } else if (item.json.url) {
      imageUrl = item.json.url;
    } else {
      error = 'No image data found in response';
    }
  }

    // Return only the image URL (not full response data) to save memory
    if (imageUrl || imageBase64) {
      const finalImageUrl = imageUrl || imageBase64;
      console.log(`Item ${i + 1}: Successfully extracted image`);
      processedItems.push({
        json: {
          success: true,
          imageUrl: finalImageUrl,
          image: finalImageUrl
        }
      });
    } else {
      processedItems.push({
        json: {
          success: false,
          error: error || 'No image data found in response',
          imageUrl: null
        }
      });
    }
  } catch (e) {
    console.error(`Item ${i + 1} error:`, e.message);
    processedItems.push({
      json: {
        success: false,
        error: e.message,
        imageUrl: null
      }
    });
  }
}

console.log('Process Response: Processed', processedItems.length, 'item(s)');

// Return all processed items
return processedItems;

// Aggregate all image results into a single response
// This processes all items efficiently without loading all data into memory at once
const items = $input.all();

console.log('Aggregating', items.length, 'image result(s)');

// Extract only image URLs (lightweight)
const images = [];
const errors = [];

for (let i = 0; i < items.length; i++) {
  const item = items[i];

  // Debug: log what we're processing
  console.log(`Processing item ${i + 1}:`, item.json ? Object.keys(item.json) : 'no json');

  if (item.json && item.json.success && item.json.imageUrl) {
    images.push(item.json.imageUrl);
    console.log(`Item ${i + 1}: Added image URL (length: ${item.json.imageUrl.length})`);
  } else if (item.json && item.json.image) {
    // Fallback: check for 'image' field
    images.push(item.json.image);
    console.log(`Item ${i + 1}: Added image from 'image' field`);
  } else if (item.json && item.json.error) {
    errors.push(`Request ${i + 1}: ${item.json.error}`);
    console.log(`Item ${i + 1}: Error - ${item.json.error}`);
  } else {
    console.log(`Item ${i + 1}: No image found, json keys:`, item.json ? Object.keys(item.json) : 'no json');
  }
}

console.log('Total images aggregated:', images.length);
console.log('Total errors:', errors.length);
console.log('First image preview:', images.length > 0 ? images[0].substring(0, 100) + '...' : 'none');

// Return aggregated result
return [{
  json: {
    success: images.length > 0,
    images: images,
    imageCount: images.length,
    errors: errors.length > 0 ? errors : null,
    totalRequests: items.length
  }
}];
