// Extract prompt and convert image files to base64
// Handle n8n's filesystem binary storage mode
const items = $input.all();
const item = items[0];

// Debug: Log what we received
console.log('Received item keys:', Object.keys(item));
console.log('Has binary:', !!item.binary);
if (item.binary) {
  console.log('Binary keys:', Object.keys(item.binary));
  Object.keys(item.binary).forEach(key => {
    console.log(`Binary ${key} type:`, typeof item.binary[key].data, 'Length:', item.binary[key].data?.length);
  });
}

// Extract prompt from body - handle different formats
let prompt = 'Create a professional, high-quality portrait photo with studio lighting, business attire, clean background';

if (item.json && item.json.body) {
  if (typeof item.json.body === 'string') {
    try {
      const parsed = JSON.parse(item.json.body);
      prompt = parsed.prompt || prompt;
    } catch (e) {
      prompt = item.json.body.prompt || prompt;
    }
  } else if (item.json.body.prompt) {
    prompt = item.json.body.prompt;
  }
} else if (item.json && item.json.prompt) {
  prompt = item.json.prompt;
}

// Extract and convert image files to base64
const imageBase64Array = [];

if (item.binary) {
  const binaryKeys = Object.keys(item.binary);

  for (let i = 1; i <= 5; i++) {
    const imageKey = `image${i}`;
    if (item.binary[imageKey]) {
      try {
        const binaryData = item.binary[imageKey];
        let base64Data = null;

        // In n8n Code node, binary data might be:
        // 1. Already base64 string in .data
        // 2. Buffer object
        // 3. Filesystem reference (needs special handling)

        if (binaryData.data) {
          // Check if it's a filesystem reference
          if (typeof binaryData.data === 'string' && binaryData.data.startsWith('filesystem-')) {
            // Skip - this is a reference, not actual data
            console.log(`Image ${i} has filesystem reference, cannot process in Code node`);
            continue;
          }

          // If it's a Buffer, convert to base64
          if (Buffer.isBuffer(binaryData.data)) {
            base64Data = binaryData.data.toString('base64');
          } else if (typeof binaryData.data === 'string') {
            // Already a string - check if it's base64 or data URL
            base64Data = binaryData.data;
            // Remove data URL prefix if present
            if (base64Data.includes(',')) {
              base64Data = base64Data.split(',')[1];
            }
          }
        }

        if (!base64Data || base64Data === '') {
          console.log(`Image ${i} has no valid data`);
          continue;
        }

        const mimeType = binaryData.mimeType || 'image/jpeg';

        // Format as data URL for OpenRouter
        imageBase64Array.push(`data:${mimeType};base64,${base64Data}`);
        console.log(`Added image ${i}:`, mimeType, 'Base64 length:', base64Data.length);
      } catch (e) {
        console.error(`Error processing image ${i}:`, e.message, e.stack);
      }
    }
  }
}

console.log('Final prompt:', prompt);
console.log('Final image count:', imageBase64Array.length);

return {
  json: {
    prompt: prompt,
    images: imageBase64Array,
    imageCount: imageBase64Array.length,
    hasImages: imageBase64Array.length > 0
  }
};

// Prepare request body for OpenRouter Gemini 2.5 Flash Image
// Format matches OpenRouter API documentation: https://openrouter.ai/google/gemini-2.5-flash-image
const items = $input.all();
const item = items[0];

// Build the request payload exactly as per OpenRouter docs
const requestBody = {
  model: 'google/gemini-2.5-flash-image',
  messages: [
    {
      role: 'user',
      content: []
    }
  ]
};

// Get the prompt
const promptText = item.json.enhancedPrompt || 'Create a professional, high-quality portrait photo with studio lighting, business attire, clean background';

// Build content array - images first, then text (as per OpenRouter example)
const contentArray = [];

// Add images if available
if (item.json.images && item.json.images.length > 0) {
  item.json.images.forEach((imageDataUrl, index) => {
    if (index < 5) { // Limit to 5 images
      contentArray.push({
        type: 'image_url',
        image_url: {
          url: imageDataUrl
        }
      });
    }
  });
}

// Add text prompt (required)
contentArray.push({
  type: 'text',
  text: promptText
});

// Set the content array
requestBody.messages[0].content = contentArray;

// Log for debugging
console.log('Request model:', requestBody.model);
console.log('Content items:', contentArray.length);
console.log('Images count:', item.json.images ? item.json.images.length : 0);
console.log('Request structure:', JSON.stringify(requestBody).substring(0, 300));

return {
  json: requestBody
};

// Extract the generated image from OpenRouter/Gemini response
const items = $input.all();
const item = items[0];

let imageUrl = '';
let imageBase64 = '';
let error = null;

// Ensure we have valid data
if (!item) {
  return {
    json: {
      success: false,
      error: 'No data received from OpenRouter API - workflow may have failed',
      imageUrl: '',
      image: ''
    }
  };
}

if (!item.json) {
  return {
    json: {
      success: false,
      error: 'No JSON data in response from OpenRouter API',
      imageUrl: '',
      image: '',
      rawData: item
    }
  };
}

console.log('Processing response:', JSON.stringify(item.json).substring(0, 500));

try {
  // Check for error first
  if (item.json.error) {
    const errorMsg = item.json.error.message || item.json.error.error?.message || JSON.stringify(item.json.error);
    error = 'OpenRouter API Error: ' + errorMsg;
    console.error('OpenRouter error:', errorMsg);
  }
  // Check for direct image generation response format (images/generations endpoint)
  else if (item.json.data && Array.isArray(item.json.data) && item.json.data[0]) {
    imageUrl = item.json.data[0].url || '';
    imageBase64 = item.json.data[0].b64_json || '';
  }
  // Check for chat completions response format
  else if (item.json.choices && item.json.choices[0]) {
    const choice = item.json.choices[0];

    if (choice.message && choice.message.content) {
      const content = choice.message.content;

      // Handle array content (multimodal)
      if (Array.isArray(content)) {
        const imageContent = content.find(c => c.type === 'image_url' || c.type === 'image');
        if (imageContent) {
          imageUrl = imageContent.image_url?.url || imageContent.url || '';
          imageBase64 = imageContent.image_url?.url || imageContent.url || '';
        }
        // Also check for text content that might contain image data
        const textContent = content.find(c => c.type === 'text');
        if (textContent && textContent.text && textContent.text.includes('data:image')) {
          imageBase64 = textContent.text;
        }
      }
      // Handle string content
      else if (typeof content === 'string') {
        if (content.includes('data:image')) {
          imageBase64 = content;
        } else if (content.startsWith('http')) {
          imageUrl = content;
        }
      }
    }
  }

  // If still no image found, check for alternative response structures
  if (!imageUrl && !imageBase64) {
    // Check for nested data structures
    if (item.json.result && item.json.result.image) {
      imageUrl = item.json.result.image;
    } else if (item.json.image) {
      imageUrl = item.json.image;
    } else if (item.json.url) {
      imageUrl = item.json.url;
    } else {
      error = 'No image data found in response. Response structure: ' + JSON.stringify(item.json).substring(0, 300);
    }
  }
} catch (e) {
  error = e.message + ' - Response: ' + JSON.stringify(item.json).substring(0, 300);
}

return {
  json: {
    success: !error && (!!imageUrl || !!imageBase64),
    imageUrl: imageUrl || imageBase64,
    image: imageBase64 || imageUrl,
    error: error,
    rawResponse: item.json
  }
};
